import logo from './logo.svg';
import './App.css';
import React, { useEffect, useRef, useState } from 'react';

function App() {
  const [context1,setContext1] = useState(null)
  const [context2,setContext2] = useState(null)
  const [drawing, setDrawing] = useState(false);
  let array1 = new Array(1152);
  let array2 = new Array(1152);
  for (var i = 0; i < 1152; i++) {
    array1[i] = 500;
    array2[i] = 280;
  }
  const [linesY, setLinesY] = useState(array1)

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    context1.fillRect(200,100,150,100);
    setLinesY(
        linesY.map((pos, index) => (index === offsetX ? offsetY : pos))
    )
    context1.beginPath();
    for(var i = 0; i < 1152; i++) {
	    context1.moveTo(i,linesY[i]);      //盛り上がり度曲線を描く
	    context1.lineTo(i+1,linesY[i+1]);
	  }
    context1.stroke();
	  context1.closePath();
    setDrawing(true);
  };

  const draw = ([ nativeEvent ]) => {
    if(!drawing) {
      return;
    }
    context1.fillRect(100,300,50,50);
  };

  const stopDraw = () => {
    context1.fillRect(500,100,50,50);
    setDrawing(false);
  };

  useEffect(()=>{
    const canvas1 = document.getElementById('canvas1');
    const ctx1 = canvas1.getContext('2d');

    const canvas2 = document.getElementById('canvas2');
    const ctx2 = canvas2.getContext('2d');
    setContext1(ctx1);
    setContext2(ctx2);
    ctx1.fillStyle = 'green';
    ctx1.fillRect(10, 10, 150, 100);
    ctx1.fillStyle = 'black';
    ctx1.strokeRect(0,0,canvas1.width,canvas1.height);
    ctx2.fillStyle = 'green';
    ctx2.fillRect(10, 10, 150, 100);

    ctx1.beginPath();
    for(var i = 0; i < 1152; i++) {
	    ctx1.moveTo(i,linesY[i]);      //盛り上がり度曲線を描く
	    ctx1.lineTo(i+1,linesY[i+1]);
	  }
    ctx1.stroke();
	  ctx1.closePath();
  },[]);

  return (
    <>
      <h1>asdf</h1>
      <canvas width="1152" height="720" id="canvas1" 
        onMouseDown={startDraw}
        onMouseUp={stopDraw}
        onMouseMove={draw}>
      </canvas>
      <h1>qwer</h1>
      <canvas width="1280" height="720" id="canvas2"></canvas>
    </>
  );
}

export default App;
