import logo from './logo.svg';
import './App.css';
import React, { useEffect, useRef, useState } from 'react';

function App() {
  const [context1,setContext1] = useState(null)
  const [context2,setContext2] = useState(null)
  const [drawing, setDrawing] = useState(false);
  let posX = 0;
  let posY = 0;
  let count = 0;
  let array1 = new Array(1152);
  let array2 = new Array(1152);
  for (var i = 0; i < 1152; i++) {
    array1[i] = 100;
    array2[i] = 100;
  }
  const [linesY, setLinesY] = useState(array1)

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    setLinesY(linesY.map((pos, index) => (index === Math.floor(offsetX) ? Math.floor(offsetY) : pos)))
	  posX = offsetX;
    posY = offsetY;
    setDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    count += 1;
    if(!drawing){return;}


    const { offsetX, offsetY } = nativeEvent;
    setLinesY(linesY.map((pos, index) => (index === offsetX ? offsetY : pos)));
    let difference = Number(Math.abs(offsetX - posX));
    count = difference;
		if(offsetX > posX) {
		    for(var i = 1; i < difference; i++) {
          let temp = Math.floor((posY+(offsetY-posY) * (posX + i - posX) / (offsetX - posX)));
			    setLinesY(linesY.map((pos, index) => (index === (posX + i) ? temp : pos)));
		    }
		}else {
		    for(var i = 1; i < difference; i++) {
          let temp = Math.floor((posY+(offsetY-posY) * (posX - i - posX) / (offsetX - posX)));
			    setLinesY(linesY.map((pos, index) => (index === (posX - i) ? temp : pos)));
		    }
		}
    context1.clearRect(0,1,1151,278);
    context1.beginPath();
    for(var i = 0; i < 1151; i++) {		
      context1.moveTo(i,linesY[i]);          //盛り上がり度曲線を描く．
      context1.lineTo(i+1,linesY[i+1]);
    }
    context1.stroke();
    posX = Math.floor(offsetX);
	  posY = Math.floor(offsetY);

  };

  const stopDraw = () => {

    context1.closePath();
    setDrawing(false);
  };

  useEffect(()=>{
    const canvas1 = document.getElementById('canvas1');
    const ctx1 = canvas1.getContext('2d');

    const canvas2 = document.getElementById('canvas2');
    const ctx2 = canvas2.getContext('2d');
    setContext1(ctx1);
    setContext2(ctx2);
    ctx1.fillStyle = 'green';
    ctx1.fillStyle = 'black';
    ctx1.strokeRect(0,0,canvas1.width,canvas1.height);
    ctx2.fillStyle = 'green';
    ctx2.fillRect(10, 10, 150, 100);

    ctx1.beginPath();
    for(var i = 0; i < 1152; i++) {
	    ctx1.moveTo(i,linesY[i]);      //盛り上がり度曲線を描く
	    ctx1.lineTo(i+1,linesY[i+1]);
	  }
    ctx1.stroke();
	  ctx1.closePath();
  });

  return (
    <>
      <p>count: <code id='count'>{linesY}</code></p>
      <h1>asdf</h1>
      <canvas width="1152" height="280" id="canvas1" 
        onMouseDown={startDraw}
        onMouseUp={stopDraw}
        onMouseMove={draw}>
      </canvas>
      <h1>qwer</h1>
      <canvas width="1280" height="720" id="canvas2"></canvas>
    </>
  );
}

export default App;
