import logo from './logo.svg';
import './App.css';
import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';

function App() {
  const [context1,setContext1] = useState(null)
  const [context2,setContext2] = useState(null)
  const [drawing, setDrawing] = useState(false);
  const [dammy, setDammy] = useState(0);
  const [count, setCount] = useState(0);
  let posX = 0;
  let posY = 0;
  const [linesY, setLinesY] = useState([])

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
	  posX = offsetX;
    posY = offsetY;
    setLinesY(linesY.map((pos, index) => (index === posX ? posY : pos)))
    setDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if(!drawing){return;}
    setCount(count+1)
    let array1 = new Array(1152);
    for(var i = 0; i < 1152; i++) {
      array1[i] = 0;
    }

    const { offsetX, offsetY } = nativeEvent;
    array1[offsetX] = offsetY;
    let difference = Number(Math.abs(offsetX - posX));
    count = difference;
		if(offsetX > posX) {
		    for(var i = 1; i < difference; i++) {
          let temp = Number((posY+(offsetY-posY) * (posX + i - posX) / (offsetX - posX)));
			    array1[posX + i] = temp;
		    }
		}else {
		    for(var i = 1; i < difference; i++) {
          let temp = Number((posY+(offsetY-posY) * (posX - i - posX) / (offsetX - posX)));
			    array1[posX - i] = temp;
		    }
		}
    context1.clearRect(0,1,1151,278);
    context1.beginPath();
    for(var i = 0; i < 1151; i++) {		
      context1.moveTo(i,array1[i]);          //盛り上がり度曲線を描く．
      context1.lineTo(i+1,array1[i+1]);
    }
    context1.stroke();
    context1.closePath();

    posX = Math.floor(offsetX);
	  posY = Math.floor(offsetY);

  };

  const stopDraw = () => {
    setDrawing(false);
  };

  useEffect(()=>{
    const canvas1 = document.getElementById('canvas1');
    const canvas2 = document.getElementById('canvas2');
    context1.fillStyle = 'green';
    context1.fillStyle = 'black';
    context1.strokeRect(0,0,canvas1.width,canvas1.height);
    context2.fillStyle = 'green';
    context2.fillRect(10, 10, 150, 100);
    setCount(9)
  },[dammy]);

  useLayoutEffect(() => {
    const canvas1 = document.getElementById('canvas1');
    const ctx1 = canvas1.getContext('2d');

    const canvas2 = document.getElementById('canvas2');
    const ctx2 = canvas2.getContext('2d');
    setContext1(ctx1);
    setContext2(ctx2);
    let array1 = new Array(1152);
    let array2 = new Array(1152);
    for (var i = 0; i < 1152; i++) {
      array1[i] = 250;
    }
    setLinesY(array1)
    ctx1.beginPath();
    for(var i = 0; i < 1151; i++) {
	    ctx1.moveTo(i,linesY[i]);      //盛り上がり度曲線を描く
	    ctx1.lineTo(i+1,linesY[i+1]);
	  }
    ctx1.stroke();
	  ctx1.closePath();
  })

  return (
    <>
      <p>count: <code id='count'>{linesY}</code></p>
      <h1>asdf</h1>
      <canvas width="1152" height="280" id="canvas1" 
        onMouseDown={startDraw}
        onMouseUp={stopDraw}
        onMouseMove={draw}>
      </canvas>
      <h1>qwer</h1>
      <canvas width="1280" height="720" id="canvas2"></canvas>
    </>
  );
}

export default App;
