import logo from './logo.svg';
import './App.css';
import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import useSound from 'use-sound';
import Sound from "./song0.wav";

function App() {
  const [context1,setContext1] = useState(null)
  const [audio, setAudio] = useState(null);
  const [context2,setContext2] = useState(null)
  const [drawing, setDrawing] = useState(false);
  const [dammy, setDammy] = useState(0);
  const [count, setCount] = useState(0);
  const [numberi, setNumberi] = useState(0);
  const [temps, setTemp] = useState(0);
  const [posX, setPosX] = useState(0);
  const [posY, setPosY] = useState(0);
  const [linesY, setLinesY] = useState([])
  let [play, { stop, pause }] = useSound(Sound);

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
	  setPosX(Math.floor(offsetX));
    setPosY(Math.floor(offsetY));
    setLinesY(linesY.map((pos, index) => (index === posX ? posY : pos)))
    setDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if(!drawing){return;}
    const { offsetX, offsetY } = nativeEvent;
    let array = new Array(1152);
    for(var i = 0; i < 1152; i++) {
      array[i] = linesY[i]
    }
    array[offsetX] = offsetY;
    setLinesY(linesY.map((pos, index) => (index === offsetX ? offsetY : pos)))
    let difference = Math.floor((Math.abs(offsetX - posX)));
		if(offsetX > posX) {
		  for(var i = 1; i < difference; i++) {
        array[posX + i] = Math.floor(posY+(offsetY-posY) * (posX + i - posX) / (offsetX - posX));
		  }
		}else {
		  for(var i = 1; i < difference; i++) {
        array[posX - i] = Math.floor(posY+(offsetY-posY) * (posX - i - posX) / (offsetX - posX));
		  }
		}
    setLinesY([...array])

    context1.clearRect(0,1,1151,278);
    context1.strokeStyle = "gray"
    for(let i = 0; i < 32; i++) {
      for(let j = 0; j < 5; j++) {
        context1.strokeRect(36*i,56*j,36,56);
      }
    }
    context1.lineWidth = "3";
    context1.strokeStyle = "blue"
    context1.beginPath();
    for(var i = 0; i < 1151; i++) {		
      context1.moveTo(i,linesY[i]);          //盛り上がり度曲線を描く．
      context1.lineTo(i+1,linesY[i+1]);
    }
    context1.stroke();
    context1.closePath();

    context1.lineWidth = "1"
    context1.strokeStyle = "black"

    setPosX(Math.floor(offsetX))
	  setPosY(Math.floor(offsetY))

  };

  const stopDraw = () => {
    setDrawing(false);
  };

  const clickRect = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    let posRectX = Math.floor(offsetX/36);
    let posRectY = Math.floor(offsetY/50);
    context2.lineWidth = 6;
    context2.strokeRect(36*posRectX,50*posRectY,36,50);
    context2.lineWidth = 1;
    let newCount = posRectX + posRectY;

    setCount(newCount); 
  };


  const test = () => {
    const test1 = new Audio(Sound)
    setCount(count+1)
    test1.play();
  }

  const createMusic = () => {
    let excitement_array = new Array(32);
    for(let i = 0; i < 32; i++) {
       //１ブロックの範囲を決定
      const block_start = Math.ceil(i * 36)
      const block_finish = Math.ceil(block_start + 36)
      //ブロックの合計から平均を計算
      let block_total = 0
      for(let j = block_start; j < block_finish; j++) {
        block_total += Math.abs((linesY[j] - 56))


      excitement_array[i] = Math.ceil(
        block_total / 36 / 56);
      }
    }
    setCount(excitement_array)
    const url = "http://127.0.0.1:5000//projects/0/songs"; 

    const data = {
      curves: excitement_array      //盛り上がり度曲線のパラメーターを格納した配列をJSONデータにする
    };

	  axios.post(url,data)       //サーバーから音素材の配列を受け取った後，then部分を実行する．
	    .then(function (response) {
        const songid = response.data.songid
        setCount(songid)
      });
  }

  useEffect(()=>{
    const canvas1 = document.getElementById('canvas1');
    const ctx1 = canvas1.getContext('2d');
    const canvas2 = document.getElementById('canvas2');
    const ctx2 = canvas2.getContext('2d');
    setContext1(ctx1);
    setContext2(ctx2);
    let array = new Array(1152);
    for (var i = 0; i < 1152; i++) {
      array[i] = 250;
    }
    setLinesY(array)
    ctx1.fillStyle = 'green';
    ctx1.fillStyle = 'black';
    ctx1.strokeStyle = "black"
    ctx1.lineWidth = "3"
    ctx1.strokeRect(0,0,canvas1.width,canvas1.height);
    ctx1.strokeStyle = "gray";
    ctx1.lineWidth = "1"
    for(let i = 0; i < 32; i++) {
      for(let j = 0; j < 5; j++) {
        ctx1.strokeRect(36*i,56*j,36,56);
      }
    }
    ctx1.lineWidth = "3";
    ctx1.strokeStyle = "blue"
    ctx1.beginPath();
    for(var i = 0; i < 1151; i++) {
	    ctx1.moveTo(i,250);      //盛り上がり度曲線を描く
	    ctx1.lineTo(i+1,250);
	  }
    ctx1.stroke();
	  ctx1.closePath();
    setCount(0)
    ctx2.strokeRect(0,0,canvas2.width,canvas2.height);
    for(let i = 0; i < 32; i++) {
      for(let j = 0; j < 4; j++) {
        ctx2.strokeRect(i*36,j*50,36,50);
      }
    }
    const url = "http://127.0.0.1:5000/projects/0/songs/0/wav"; 

	  axios.get(url,{responseType: 'blob',})       //サーバーから音素材の配列を受け取った後，then部分を実行する．
	    .then(function (response) {
        var FILE = window.URL.createObjectURL(new Blob([response.data], {type: 'audio/wav'}));
        const newCount = count+1;
        setCount(FILE)
        const test1 = new Audio(FILE);
        setAudio(test1)
      });
  },[dammy]);

  const test1 = context => {
    context.pause();
    context.currentTime = 0;
  }

  return (
    <>
      <p>count: <code id='count'>{linesY}</code></p>
      <p>count: <code id='count'>{temps}</code></p>
      <p>count: <code id='count'>{count}</code></p>
      <p>count: <code id='count'>{numberi}</code></p>
      <button type="button" onClick={() => audio.play()}>play</button>
      <button type="button" onClick={() => audio.pause()}>pause</button>
      <button type="button" onClick={() => {audio.pause(); audio.currentTime = 0}}>stop</button>
      <button type='button' onClick={() => createMusic()}>create</button>
      <button type="button" onClick={() => test1(audio)}>test</button>
      <button type="button" onClick={() => test()}>play1</button>
      <h1>asdf</h1>
      <canvas x="100" y="100" width="1152" height="280" id="canvas1" 
        onMouseDown={startDraw}
        onMouseUp={stopDraw}
        onMouseMove={draw}>
      </canvas>
      <h1>qwer</h1>
      <canvas width="1152" height="200" id="canvas2" onMouseDown={clickRect}></canvas>
    </>
  );
}

export default App;
